bash.body:
bash '${1:name}' do
  code <<-EOH
  ${5:script commands}
  EOH
  action :${2:run}
end

batch.body:
batch '${1:name}' do
  code <<-EOH
  ${5:batch commands}
  EOH
  action :${2:run}
end

csh.body:
csh '${1:name}' do
  code <<-EOH
  ${5:script commands}
  EOH
  action :${6:run}
end

reboot.body:
reboot '${1:name}' do
  action :${2:request_reboot}
  reason '${3:reason}'
  delay_mins ${4:minutes}
end

registry_key.body:
registry_key '${1:key_name}' do
  values [{
    name: '${2:name}',
    type: '${3:type}',
    data: '${4:data}'
  }]
action :${5:create}
end

ruby_block.body:
ruby_block '${1:name}' do
  block do
    ${2:code}
  end
  action :${3:run}
end
